import React, { useState } from "react";
import { Button } from "../ui/button";

export default function AdminSettingsPage() {
  const [categories, setCategories] = useState(["Refund", "Order", "Shipping"]);
  const [newCategory, setNewCategory] = useState("");
  const [responses, setResponses] = useState(["Thank you for contacting us.", "We are looking into your issue."]);
  const [newResponse, setNewResponse] = useState("");

  const addCategory = () => {
    if (newCategory.trim()) {
      setCategories([...categories, newCategory.trim()]);
      setNewCategory("");
    }
  };

  const addResponse = () => {
    if (newResponse.trim()) {
      setResponses([...responses, newResponse.trim()]);
      setNewResponse("");
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <h1 className="text-2xl font-bold mb-6">Admin Settings</h1>
      <div className="bg-white rounded-xl shadow p-6 mb-8">
        <h2 className="font-semibold mb-2">Categories</h2>
        <ul className="mb-2">
          {categories.map((cat, idx) => (
            <li key={idx} className="mb-1">{cat}</li>
          ))}
        </ul>
        <div className="flex gap-2">
          <input
            className="border rounded px-2 py-1"
            placeholder="Add category"
            value={newCategory}
            onChange={e => setNewCategory(e.target.value)}
          />
          <Button className="bg-green-600 text-white" onClick={addCategory}>Add</Button>
        </div>
      </div>
      <div className="bg-white rounded-xl shadow p-6">
        <h2 className="font-semibold mb-2">Canned Responses</h2>
        <ul className="mb-2">
          {responses.map((res, idx) => (
            <li key={idx} className="mb-1">{res}</li>
          ))}
        </ul>
        <div className="flex gap-2">
          <input
            className="border rounded px-2 py-1"
            placeholder="Add canned response"
            value={newResponse}
            onChange={e => setNewResponse(e.target.value)}
          />
          <Button className="bg-green-600 text-white" onClick={addResponse}>Add</Button>
        </div>
      </div>
    </div>
  );
}
