import React from "react";
import { Button } from "../ui/button";
import { Link } from "react-router-dom";

export default function TicketDetail() {
  // Replace with real data
  const ticket = {
    id: 1,
    subject: "Unable to Login",
    status: "Open",
    description: "I am unable to login to my account. Every time I try, I get an error message.",
    priority: "High",
    createdAt: "2025-06-02",
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-400 via-green-500 to-green-700 relative">
      {/* Tokopedia Helpdesk Watermark */}
      <div className="absolute top-6 left-10 z-10 flex items-center space-x-2 select-none">
        <span className="text-2xl font-bold text-white drop-shadow-lg tracking-wide">
          Tokopedia Helpdesk
        </span>
      </div>
      <div className="flex flex-col items-center justify-center min-h-screen">
        <div className="bg-white rounded-2xl shadow-2xl border border-green-200 p-10 w-full max-w-2xl">
          <div className="mb-4">
            <Link to="/view-tickets" className="text-green-700 hover:underline font-semibold">
              &larr; Back to Tickets
            </Link>
          </div>
          <h1 className="text-3xl font-bold mb-4 text-green-700">{ticket.subject}</h1>
          <div className="mb-4">
            <span className="inline-block bg-green-100 text-green-700 px-3 py-1 rounded-full mr-2 font-semibold">
              {ticket.status}
            </span>
            <span className="inline-block bg-gray-100 text-gray-700 px-3 py-1 rounded-full font-semibold">
              {ticket.priority}
            </span>
          </div>
          <div className="mb-6">
            <p className="text-gray-700">{ticket.description}</p>
          </div>
          <div className="text-sm text-gray-500 mb-6">
            Created at: {ticket.createdAt}
          </div>
          <Button className="bg-green-600 hover:bg-green-700 text-white py-2 rounded-md">
            Close Ticket
          </Button>
        </div>
      </div>
    </div>
  );
}
