import React from "react";
import { Button } from "../ui/button";
import { useNavigate } from "react-router-dom";

export default function MyDetailsPage() {
  const navigate = useNavigate();

  // Replace with real user data as needed
  const user = {
    name: "John Doe",
    email: "john.doe@email.com",
    role: "User",
    avatar: "https://ui-avatars.com/api/?name=John+Doe&background=10b981&color=fff"
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-green-200 to-white flex flex-col">
      {/* Header */}
      <header className="bg-white shadow flex items-center justify-between px-8 py-4 sticky top-0 z-10">
        <span className="text-green-600 font-bold text-xl">Tokopedia HelpDesk</span>
        <Button
          className="text-green-700 hover:underline font-semibold"
          variant="ghost"
          onClick={() => navigate("/")}
        >
          &larr; Back to Dashboard
        </Button>
      </header>
      <main className="flex-1 flex flex-col items-center justify-center px-4">
        <div className="bg-white rounded-2xl shadow-2xl border border-green-200 p-10 w-full max-w-md mt-12">
          <div className="flex flex-col items-center mb-6">
            <img src={user.avatar} alt="User" className="h-20 w-20 rounded-full mb-4" />
            <div className="text-2xl font-bold text-green-700">{user.name}</div>
            <div className="text-gray-500">{user.email}</div>
            <div className="text-sm text-gray-400 mt-1">{user.role}</div>
          </div>
          <div className="mt-6">
            <Button className="w-full bg-green-600 hover:bg-green-700 text-white py-2 rounded-md">
              Edit Details
            </Button>
          </div>
        </div>
      </main>
    </div>
  );
}
